import React, { useState } from 'react';
import axios from 'axios';
import {useNavigate } from 'react-router-dom';

const SignUp = () => {
    const navigate = useNavigate();
  const [formData, setFormData] = useState({
    name: '',
    email: '',
    password: '',
    password_confirmation:''
  });

  const handleChange = (e) => {
    setFormData({ ...formData, [e.target.name]: e.target.value });
  };

  const handleSignUp = async () => {
    debugger
    try {
      // Make API call to sign up using Axios
      const response = await axios.post('http://localhost:3001/signup', formData);

      // Handle response as needed
      console.log(response.data);

      // Optionally, redirect to login after successful sign-up
      navigate("/");
    } catch (error) {
      console.error('Error signing up:', error);
    }
  };

  return (
    <div>
      <h2>Sign Up</h2>
      <input type="text" name="user" placeholder="Username" onChange={handleChange} />
      <input type="email" name="email" placeholder="Email" onChange={handleChange} />
      <input type="password" name="password" placeholder="Password" onChange={handleChange} />
      <input type="password" name="password_confirmation" placeholder="Password_confirmation" onChange={handleChange} />
      <button onClick={handleSignUp}>Sign Up</button>
    </div>
  );
};

export default SignUp;
