// PostsList.js

import React, { useState, useEffect } from 'react';
import axios from 'axios';
import { Link, Routes, Route, Outlet, useNavigate, useParams } from 'react-router-dom';
import ShowList from './ShowList';

const Post = ({ post, onShowPost }) => (
  <tr key={post.id}>
    <td><strong>{post.title}</strong></td>
    <td>{post.content}</td>
    <td>
      <Link to={`showpost/${post.id}`} className="btn btn-primary">
        Show Post
      </Link>
    </td>
  </tr>
);

const PostsList = () => {
  const [posts, setPosts] = useState([]);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState(null);
  const [showSinglePost, setShowSinglePost] = useState(null);

  useEffect(() => {
    // Fetch all posts from your API using Axios
    axios.get('http://localhost:3001/api/posts')
      .then(response => {
        setPosts(response.data);
        setLoading(false);
      })
      .catch(error => {
        setError(error);
        setLoading(false);
      });
  }, []); // Run this effect only once when the component mounts

  const handleShowPost = (post) => {
    setShowSinglePost(post);
  };

  const handleClosePost = () => {
    setShowSinglePost(null);
  };

  if (loading) {
    return <p>Loading...</p>;
  }

  if (error) {
    return <p>Error: {error.message}</p>;
  }

  return (
    <div>
      <h2>All Posts</h2>
      <table className="table">
        <thead>
          <tr>
            <th scope="col">Title</th>
            <th scope="col">Content</th>
            <th scope="col">Actions</th>
          </tr>
        </thead>
        <tbody>
          {posts.map(post => (
            <Post key={post.id} post={post} onShowPost={handleShowPost} />
          ))}
        </tbody>
      </table>

      <Routes>
        <Route path="showlist/:postId" element={<ShowList onClose={handleClosePost} />} />
      </Routes>

      <Link to="/addpost" className="btn btn-primary">
        Add Post
      </Link>

      <Outlet />
    </div>
  );
};

export default PostsList;
