// SinglePost.js

import React from 'react';

const ShowList = ({ post, onClose }) => (
  <div>
    <h2>{post.title}</h2>
    <p>{post.content}</p>
    <button onClick={onClose} className="btn btn-secondary">
      Close
    </button>
  </div>
);

export default ShowList;
