  }

  return (
    <div>
      <h2>Post Details</h2>
      <p><strong>Title:</strong> {post.title}</p>
      <p><strong>Content:</strong> {post.content}</p>
      {/* Add more details as needed */}
      <button onClick={handleClosePost}>
        Close
      </button>
    </div>
  );
};

export default ShowList;
