// ShowList.js

import React from 'react';
import { useParams } from 'react-router-dom';

const ShowList = ({ onClose }) => {
  const { postId } = useParams();

  // Fetch the detailed post data using the postId, for example:
  // const detailedPost = ...; // Fetch the detailed post from your API

  return (
    <div>
      {/* Display the detailed post information */}
      <h2>Post Details</h2>
      <p>Post ID: {postId}</p>
      {/* Add more details as needed */}
      <button onClick={onClose} className="btn btn-secondary">
        Close
      </button>
    </div>
  );
};

export default ShowList;
